clear; clc;%++++++++++++++++++++++++++++++++++++++++++++++% NOTE: To change tomato distribution modify % function tomatornd.m and rejection_prob.m%++++++++++++++++++++++++++++++++++++++++++++++% Solution Parameters (grams)Standard_Box_Weight = 15*(453.5970244) ;Shrink_factor_min = 0.0097;Shrink_factor_max = 0.0361;Max_Box_Weight = Standard_Box_Weight*(1 + Shrink_factor_max);Min_Box_Weight = Standard_Box_Weight*(1 + Shrink_factor_min);Max_tomato_weight = 445; Nonconforming_fraction = 0.05;N_Box_Sample = 3;% 1) Find initial ranges for total conforming boxescali =       [  16  18  20  22   25   28   32   35  37  45]';slack_cali = [  80  80  70  70   60   60   50   50  50  50]';[lmin0 lmax0] = set_initial_ranges(Min_Box_Weight, Max_tomato_weight, cali);[p wbox pilon] = fnonconforming(Min_Box_Weight, Max_Box_Weight, cali, lmin0, lmax0);format short gr1 = [cali lmin0 lmax0-1 p wbox pilon]% 2) Set the tomato weight distribution%hist(tomatornd(10000,1), 100);% 3) Set limits with a non-conforming fraction[cali lmin lmax] = upgrade_limits(Nonconforming_fraction, Min_Box_Weight, Max_Box_Weight, cali, lmin0, lmax0, slack_cali);[p wbox pilon] = fnonconforming(Min_Box_Weight, Max_Box_Weight, cali, lmin, lmax);format short gr2 = [cali lmin lmax-1 p wbox pilon]% Compute Acceptance Probability for a lotpr = rejection_prob(N_Box_Sample, Min_Box_Weight, cali, lmin, lmax);r3 = [cali pr]