function pr = rejection_prob(nboxes, minboxw, cali, lmin, lmax)% Compute acceptance probability of lot% Normal distribution parametersmu = 300;sigma = 100;NS = 100000;pr = zeros(length(cali),1);for i=1:length(cali)  n = cali(i)*nboxes;%  pcali = normcdf(lmax(i),mu,sigma)-normcdf(lmin(i),mu,sigma);  x = [ lmin(i) lmax(i)];  y = normpdf(x, mu, sigma);%  y = unifpdf(x, lmin(i), lmax(i));%  y = tpdf((x-mu)/sigma, 6);%  y = lognpdf(x, log(mu), sigma);  M = traprnd(x, y, NS*n);  Mc = M(M >= lmin(i) & M < lmax(i)) ;  MR = reshape(Mc(1:(NS*n)),NS,n);  pr(i) = sum(mean(MR,2)*cali(i) < minboxw)/NS;endfor